import React, { useState } from 'react';
import styled from 'styled-components'
import searchicon from '../search.svg';

const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
`;

const FormContainer = styled.div`
    width: 400px;
    height: 70px;
    border-radius: 10px;
    background: white;
    box-shadow: 0 6px 30px -10px #d5dbed;
    margin-bottom: 10px;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const FormTab = styled.div`
    width: 95%;
    background: transparent;
    height: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
`

const SearchField = styled.div`
    width: 75%;
    height: 50px;
    background-color: #ECF1F4;
    border-radius: 6px;
    position: relative;
    overflow: hidden;
    z-index: 2;
`;

const SearchButton = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    text-transform: uppercase;
    cursor: pointer;
    width: 25%;
`

const SearchIcon = styled.img`
    height: 40px;
    position: absolute;
    top: 5px;
    left: 8px;
`

const RecentContainer = styled.div`

`;

const TextField = styled.input`
    background: transparent;
    font-size: 1.1em;
    color: #3f3f3f;
    border: none;
    width: 100%;
    height: 100%;
    padding: 5px 5px 5px 60px;
`

const PlaceHolder = styled.p`
    position: absolute;
    left: 60px;
    z-index: 1;
    color: #6e80a5;
    opacity: 0.8;
`

const Form = styled.form`
    position: absolute;
    width: 100%;
    height: 100%;
`

const Search = () => {
    const [active, setActive] = useState(false);
    const [query, setQuery] = useState("");

    return(
        <Wrapper onClick={() => setActive(false)}>
            <FormContainer>
                <FormTab>
                    <SearchField onClick={() => { setActive(true); console.log("search field") } }>
                        <SearchIcon src={searchicon}/>
                        {!active ? <PlaceHolder>Documents</PlaceHolder> : <></>}
                        <Form>
                            <TextField
                                autoComplete={"off"}
                                type={"text"}
                                pattern={"\S+.*"}
                                name={"input"}
                                id={"input"}
                            />
                        </Form>
                    </SearchField>
                    <SearchButton>
                        <p>search</p>
                    </SearchButton>
                </FormTab>
            </FormContainer>
            <RecentContainer>

            </RecentContainer>
        </Wrapper>
    )
}

export default Search;
